import React, { useEffect, useState } from 'react';

function App() {
  const [socket, setSocket] = useState(null);
  const [userId] = useState(`user-${Math.floor(Math.random() * 1000)}`); // temporary ID
  const [poseData, setPoseData] = useState(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    // Connect to WebSocket backend
    const ws = new WebSocket(`ws://localhost:8000/ws/${userId}`);

    ws.onopen = () => {
      console.log('WebSocket connected');
      setConnected(true);
    };

    ws.onmessage = (event) => {
      console.log('Received:', event.data);
      setPoseData(event.data);
    };

    ws.onclose = () => {
      console.log('WebSocket disconnected');
      setConnected(false);
    };

    setSocket(ws);

    // Clean up on unmount
    return () => {
      ws.close();
    };
  }, [userId]);

  const handleSendTestPose = () => {
    if (socket && connected) {
      const samplePose = {
        user_id: userId,
        landmarks: [{ x: 0.1, y: 0.2 }, { x: 0.3, y: 0.4 }] // Simulated landmarks
      };
      socket.send(JSON.stringify(samplePose));
    }
  };

  return (
    <div className="App" style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h1>Dance Battle App ðŸ’ƒðŸ•º</h1>
      <p>Welcome, <strong>{userId}</strong></p>
      <p>Status: {connected ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}</p>

      <button onClick={handleSendTestPose} disabled={!connected}>
        Send Test Pose Data
      </button>

      {poseData && (
        <div style={{ marginTop: '1rem' }}>
          <h3>Incoming Pose Data</h3>
          <pre>{poseData}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
