import React, { useEffect, useRef, useState } from "react";

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCameraReady, setIsCameraReady] = useState(false);

  useEffect(() => {
    const videoElement = videoRef.current;
    const canvasElement = canvasRef.current;
    const canvasCtx = canvasElement.getContext("2d");

    // Initialize Pose model
    const pose = new window.Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    pose.onResults((results) => {
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      canvasCtx.drawImage(
        results.image,
        0,
        0,
        canvasElement.width,
        canvasElement.height
      );

      if (results.poseLandmarks) {
        window.drawConnectors(canvasCtx, results.poseLandmarks, window.POSE_CONNECTIONS, {
          color: "#00FF00",
          lineWidth: 2,
        });
        window.drawLandmarks(canvasCtx, results.poseLandmarks, {
          color: "#FF0000",
          lineWidth: 1,
        });
      }
      canvasCtx.restore();
    });

    const startCamera = async () => {
      if (videoElement.srcObject) return; // Avoid reinitializing the camera if it's already running

      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: false,
      });
      videoElement.srcObject = stream;
      videoElement.play();
      setIsCameraReady(true); // Update state when camera is ready
    };

    // Start camera only if the component is mounted
    if (!isCameraReady) {
      startCamera();
    }

    const detectPose = async () => {
      if (videoElement.readyState === 4) {
        await pose.send({ image: videoElement });
      }
      requestAnimationFrame(detectPose);
    };

    detectPose();
  }, [isCameraReady]);

  return (
    <div className="App">
      <h2>Dance Battle Pose Detection ðŸŽ¥ðŸ•º</h2>
      <video
        ref={videoRef}
        style={{ display: "none" }}
        width="640"
        height="480"
      />
      <canvas
        ref={canvasRef}
        width="640"
        height="480"
        style={{ border: "2px solid black", borderRadius: "12px" }}
      />
    </div>
  );
}

export default App;
